import React from 'react';

import {
  AppBar,
  Flex,
  IconButton,
  Box,
  Divider,
  Typography,
  css,
  Avatar,
  Dialog,
  Backdrop,
  List,
  Snackbar,
  Spacer,
  Spinner,
  Menu,TextBox,
  useComponentState,
} from './components/UI';  
import useApp from './hooks/useApp';
import { Smiley, Close, Hamburger, Search } from './icons';

import './style.css';

export default function App() {
  const { search } = useApp()
  const {state, toggle, setState} = useComponentState({open: false});
  const { open, param, results } = state;
  return (
    <div  className="App">
     <AppBar className="toolbar">
       <IconButton ml={12} mr={3}>
         <Smiley />
       </IconButton>
       <Box className="toolbar-button logo">Rachet-UI</Box>
       <Box  onClick={() => window.open('http://docs.ratchet-ui.com')} className="toolbar-button">Docs</Box>
       <Box  onClick={() => window.open('http://sql.ratchet-ui.com')}  className="toolbar-button">Ratchet-SQL</Box>

       <Spacer />
        <Flex onClick={() => toggle('open')} align="center" className="search-box" mr={16}>
          <Search style={{width:16, height: 16, marginRight: 8}}/>
          Search...
          <Spacer />
          <Box className="search-k" >Ctrl+K</Box>
        </Flex>
     </AppBar>
    <Dialog onClose={() => toggle('open')} open={open} width="500px" height="500px">
      <Flex>
        <TextBox fullWidth placeholder="Search components" value={param} onChange={(v)  =>setState('results', search(v.target.value))} />
      </Flex>
      <Divider/>
      {!!results && <List items={results.map(b => <Box mt={2} mb={2} >{b}</Box>)} />}
    </Dialog>
    </div>
  );
}


// https://docs.ratchet-ui.com/?path=/docs/components-data-display--avatar

const linkOf = (desc) => {
  const array = desc.split(':')
  const cat = array[1].trim().replace(/\s/g, '-')
  const com = array[2].trim().replace(/\s/g, '-')
};
